function initMap(){pos=new google.maps.LatLng(28.5714851,77.2591487),map=new google.maps.Map(document.getElementById("map"),{zoom:15,center:pos,mapTypeControl:!1}),map.addListener("idle",runNearbySearch)}function runNearbySearch(){new google.maps.places.PlacesService(map).nearbySearch({bounds:map.getBounds(),radius:"2000",rankBy:google.maps.places.RankBy.PROMINENCE},function(a,b){makeMarkers(a)})}function makeMarkers(a){a.forEach(function(a){var b=new google.maps.Marker({position:a.geometry.location,map:map});markers.push(b)})}document.querySelector(".arrow").addEventListener("click",function(a){document.querySelector(".searchAndList").classList.toggle("slipIn"),document.querySelector(".arrow").classList.toggle("floatRight")});var markers=[],map,pos;